import createAPI from 'create-api';
import ServiceLocator from '../services/ServiceLocator';

const logger = ServiceLocator.$logger();
const logRequests = !!process.env.DEBUG_API;

const api = createAPI({
    version: '/v0',
    config: {
        databaseURL: 'https://hacker-news.firebaseio.com',
    },
});

// warm the front page cache every 15 min
// make sure to do this only once across all requests
if (api.onServer) {
    warmCache();
}

function warmCache() {
    fetchItems((api.cachedIds.top || []).slice(0, 30));
    setTimeout(warmCache, 1000 * 60 * 15);
}

function fetch(child) {
    if (logRequests) logger.info(`fetching ${child}...`);
    const cache = api.cachedItems;
    if (cache && cache.has(child)) {
        if (logRequests) logger.info(`cache hit for ${child}.`);
        return Promise.resolve(cache.get(child));
    }
    return new Promise((resolve, reject) => {
        api.child(child).once(
            'value',
            snapshot => {
                const val = snapshot.val();
                // mark the timestamp when this item is cached
                if (val) val.__lastUpdated = Date.now();
                if (cache) cache.set(child, val);
                if (logRequests) logger.info(`fetched ${child}.`);
                resolve(val);
            },
            reject
        );
    });
}

export function fetchIdsByType(type) {
    return api.cachedIds && api.cachedIds[type] ? Promise.resolve(api.cachedIds[type]) : fetch(`${type}stories`);
}

export function fetchItem(id) {
    return fetch(`item/${id}`);
}

export function fetchItems(ids) {
    return Promise.all(ids.map(id => fetchItem(id)));
}

export function fetchUser(id) {
    return fetch(`user/${id}`);
}

export function watchList(type, cb) {
    let first = true;
    const ref = api.child(`${type}stories`);
    const handler = snapshot => {
        if (first) {
            first = false;
        } else {
            cb(snapshot.val());
        }
    };
    ref.on('value', handler);
    return () => {
        ref.off('value', handler);
    };
}
import Firebase from 'firebase';
import LRU from 'lru-cache';

export default function createAPI({ config, version }) {
    let api;
    // this piece of code may run multiple times in development mode,
    // so we attach the instantiated API to `process` to avoid duplications
    if (process.__API__) {
        api = process.__API__;
    } else {
        Firebase.initializeApp(config);
        process.__API__ = Firebase.database().ref(version);
        api = process.__API__;
        api.onServer = true;

        // fetched item cache
        api.cachedItems = new LRU({
            max: 1000,
            maxAge: 1000 * 60 * 15, // 15 min cache
        });

        // cache the latest story ids
        api.cachedIds = {};
        ['top', 'new', 'show', 'ask', 'job'].forEach(type => {
            api.child(`${type}stories`).on('value', snapshot => {
                api.cachedIds[type] = snapshot.val();
            });
        });
    }
    return api;
}
import { fetchUser, fetchItems, fetchIdsByType } from '../../../api';

export default {
    // ensure data for rendering given list type
    FETCH_LIST_DATA: ({ commit, dispatch }, { type }) => {
        commit('SET_ACTIVE_TYPE', { type });
        return fetchIdsByType(type)
            .then(ids => commit('SET_LIST', { type, ids }))
            .then(() => dispatch('ENSURE_ACTIVE_ITEMS'));
    },

    // ensure all active items are fetched
    ENSURE_ACTIVE_ITEMS: ({ dispatch, getters }) => {
        return dispatch('FETCH_ITEMS', {
            ids: getters.activeIds,
        });
    },

    FETCH_ITEMS: ({ commit, state }, { ids }) => {
        // on the client, the store itself serves as a cache.
        // only fetch items that we do not already have, or has expired (3 minutes)
        const now = Date.now();
        ids = ids.filter(id => {
            const item = state.items[id];
            if (!item) {
                return true;
            }
            if (now - item.__lastUpdated > 1000 * 60 * 3) {
                return true;
            }
            return false;
        });
        if (ids.length) return fetchItems(ids).then(items => commit('SET_ITEMS', { items }));
        return Promise.resolve();
    },

    FETCH_USER: ({ state, dispatch }, { id }) => {
        return state.users[id]
            ? Promise.resolve(state.users[id])
            : fetchUser(id).then(user => dispatch('SET_USER', { id, user }));
    },

    SET_USER: ({ commit, state }, { id }) => {
        return state.users[id]
            ? Promise.resolve(state.users[id])
            : fetchUser(id).then(user => commit('SET_USER', { id, user }));
    },
};
import Vue from 'vue';

export default {
    SET_ACTIVE_TYPE: (state, { type }) => {
        state.activeType = type;
    },

    SET_LIST: (state, { type, ids }) => {
        state.lists[type] = ids;
    },

    SET_ITEMS: (state, { items }) => {
        items.forEach(item => {
            if (item) Vue.set(state.items, item.id, item);
        });
    },

    SET_USER: (state, { id, user }) => {
        Vue.set(state.users, id, user || false); /* false means user not found */
    },
};
export default {
    // ids of the items that should be currently displayed based on
    // current list type and current pagination
    activeIds(state, getters, rootState) {
        const { activeType, itemsPerPage, lists } = state;

        if (!activeType) {
            return [];
        }

        const page = Number(rootState.route.params.page) || 1;
        const start = (page - 1) * itemsPerPage;
        const end = page * itemsPerPage;

        return lists[activeType].slice(start, end);
    },

    // items that should be currently displayed.
    // this Array may not be fully fetched.
    activeItems(state, getters) {
        return getters.activeIds.map(id => state.items[id]).filter(_ => _);
    },
};
import actions from './actions';
import mutations from './mutations';
import getters from './getters';

/**
 * Function for create store instance.
 * Функция создания экземпляра стора.
 */
export default {
    name: 'item_list',
    namespaced: true,
    state: {
        activeType: null,
        itemsPerPage: 20,
        items: {
            /* [id: number]: Item */
        },
        users: {
            /* [id: string]: User */
        },
        lists: {
            top: [
                /* number */
            ],
            new: [],
            show: [],
            ask: [],
            job: [],
        },
    },
    actions,
    mutations,
    getters,
};

