import Vue from 'vue';
import { sync } from 'vuex-router-sync';
import createStore from './store';
import createRouter from './router';

import './plugins/i18n';
import media from './plugins/media';

import titleMixin from './util/title';
import * as filters from './util/filters';

import App from './App.vue';

// mixin for handling title
Vue.mixin(titleMixin);

// register global utility filters.
Object.keys(filters).forEach(key => {
    Vue.filter(key, filters[key]);
});

// Expose a factory function that creates a fresh set of store, router,
// app instances on each call (which is called for each SSR request)
export default function createApp() {
    // create store and router instances
    const store = createStore();
    const router = createRouter();

    /* 
       sync the router with the vuex store.
       this registers `store.state.route`

       Синхронизация стора приложения и роутера. 
       в сторе появляется модуль route для роутера
    */
    sync(store, router);

    // create the app instance.
    // here we inject the router, store and ssr context to all child components,
    // making them available everywhere as `this.$router` and `this.$store`.
    const app = new Vue({
        router,
        store,
        mq: media,
        render: h => h(App),
    });

    // expose the app, the router and the store.
    // note we are not mounting the app here, since bootstrapping will be
    // different depending on whether we are in a browser or on the server.
    return { app, router, store };
}

class BaseCookie {
    constructor(cookieString) {
        this.cookieString = `${cookieString}`;
    }

    get(name, def) {
        const matches = this.cookieString.match(
            new RegExp(`(?:^|; )${name.replace(/([$()*+./?[\\\]^{|}])/g, '\\$1')}=([^;]*)`)
        );
        return matches ? decodeURIComponent(matches[1]) : def;
    }

    set(name, value, props) {
        throw new Error('Переопредели set');
    }

    remove(name) {
        throw new Error('Переопредели remove');
    }

    prepareSetCookieString(name, value, props) {
        props = props || {};

        props.path = props.path || '/';
        let exp = props.expires;

        if (typeof exp === 'number' && exp) {
            const d = new Date();
            d.setTime(d.getTime() + exp * 1000);
            exp = props.expires = d;
        }

        if (exp && exp.toUTCString) {
            props.expires = exp.toUTCString();
        }

        value = encodeURIComponent(value);
        let updatedCookie = `${name}=${value}`;
        /* eslint-disable */
        for (const propName in props) {
            if (!props.hasOwnProperty(propName)) continue;
            updatedCookie += `; ${propName}`;
            const propValue = props[propName];

            if (propValue !== true) updatedCookie += `=${propValue}`;
        }
        /* eslint-enable */

        return updatedCookie;
    }
}

export class BrowserCookie extends BaseCookie {
    constructor() {
        super(document.cookie);
    }

    set(name, value, props) {
        document.cookie = this.prepareSetCookieString(name, value, props);
    }

    remove(name) {
        this.set(name, null, { expires: -1 });
    }
}

export class ExpressCookie extends BaseCookie {
    constructor(req, res) {
        super(req.headers.cookie);
        this.req = req;
        this.res = res;
    }

    set(name, value, props) {
        let cookies = this.res.getHeader('Set-Cookie');
        if (!cookies) cookies = [this.prepareSetCookieString(name, value, props)];
        else cookies.push(this.prepareSetCookieString(name, value, props));
        this.res.setHeader('Set-Cookie', cookies);
    }

    remove(name) {
        this.set(name, null, { expires: -1 });
    }
}

import createApp from './app';
import ServiceLocator from './services/ServiceLocator';
import { ExpressLogger } from './services/LogService';
import { ExpressCookie } from './services/CookieService';

// This exported function will be called by `bundleRenderer`.
// This is where we perform data-prefetching to determine the
// state of our application before actually rendering it.
// Since data fetching is async, this function is expected to
// return a Promise that resolves to the app instance.
export default context => {
    return new Promise((resolve, reject) => {
        const { app, router, store } = createApp();

        ServiceLocator.createInstance()
            .register('router', () => router)
            .register('store', () => store)
            .register('logger', () => new ExpressLogger())
            .register('cookie', () => new ExpressCookie(context.req, context.res));

        const { url } = context;
        const { fullPath } = router.resolve(url).route;

        if (fullPath !== url) {
            ServiceLocator.$logger().error(`full path ${fullPath} doesn't match ${url}`);
            reject({ url: fullPath });
        }

        // set router's location
        router.push(url);

        router.onReady(() => {
            // This `rendered` hook is called when the app has finished rendering
            context.rendered = () => {
                // After the app is rendered, our store is now
                // filled with the state from our components.
                // When we attach the state to the context, and the `template` option
                // is used for the renderer, the state will automatically be
                // serialized and injected into the HTML as `window.__INITIAL_STATE__`.
                context.state = store.state;
